$(document).ready(function(){$("#twitter-quotes").slick({dots:!0,arrows:!1,infinite:!0,speed:300,slidesToShow:1,adaptiveHeight:!0,appendDots:$("#the-dots")}),$('a[href^="#"]').on("click",function(t){t.preventDefault();var e=this.hash;$target=$(e),$("html, body").stop().animate({scrollTop:parseInt($target.offset().top,10)},900,"easeInOutExpo",function(){window.location.hash=e})})});